import React, { useState } from "react";
import { makeStyles, Typography, Paper, Button } from "@material-ui/core";

import { useQuery } from "@apollo/react-hooks";
import { NOTE_QUERY } from "../gql";
import { useToggle } from "../utilities";
import { EditNote } from "../components/Notes";

const useStyles = makeStyles(theme => ({
  subheading: {
    padding: theme.spacing(2),
    marginTop: theme.spacing(2),
    marginBottom: theme.spacing(2),
    backgroundColor: theme.palette.primary.main
  },
  noteContainer: {
    maxWidth: "800px",
    marginLeft: "auto",
    marginRight: "auto"
  },
  button: {
    marginTop: theme.spacing(2),
    marginBottom: theme.spacing(2)
  }
}));

export const NoteDetailPage = ({ noteId }) => {
  const [currentId, setCurrentId] = useState(noteId);
  const [deleteActive, setDeleteActive] = useState(false);

  const { data, loading, error } = useQuery(NOTE_QUERY, {
    variables: { id: currentId }
  });

  const { on: editOn, toggle: editToggle } = useToggle();

  const ReactMarkdown = require("react-markdown");
  const classes = useStyles();

  return (
    <>
      {loading && <h1>Loading...</h1>}
      {error && <h3>{error.message}</h3>}
      {data && (
        <>
          <Typography variant="h3" align="center" gutterBottom>
            {`${data.note.title}`}
          </Typography>
          {!editOn && (
            <>
              <Typography
                className={classes.subheading}
                variant="subtitle1"
                align="center"
              >
                {data.note.summary}
              </Typography>
              <Paper className={classes.noteContainer} elevation={0}>
                <ReactMarkdown source={data.note.bodyText} />
                {!deleteActive && (
                  <Button className={classes.button} size="large">
                    Delete
                  </Button>
                )}
                <Button
                  className={classes.button}
                  color="primary"
                  variant="outlined"
                  size="large"
                  onClick={editToggle}
                >
                  Edit
                </Button>
              </Paper>
            </>
          )}
          {editOn && (
            <>
              <EditNote note={data.note} toggle={editToggle} />
            </>
          )}
        </>
      )}
    </>
  );
};
