import React from "react";
import { makeStyles, Typography, Paper, Button } from "@material-ui/core";

import { useQuery } from "@apollo/react-hooks";
import { NOTE_QUERY } from "../gql";
import { useToggle } from "../utilities";

const useStyles = makeStyles(theme => ({
  subheading: {
    padding: theme.spacing(2),
    marginTop: theme.spacing(2),
    marginBottom: theme.spacing(2),
    backgroundColor: theme.palette.primary.main
  },
  noteContainer: {
    maxWidth: "800px",
    marginLeft: "auto",
    marginRight: "auto"
  }
}));

export const NoteDetailPage = ({ noteId }) => {
  const { data, loading, error } = useQuery(NOTE_QUERY, {
    variables: { id: noteId }
  });

  const { on: editOn, toggle: editToggle } = useToggle();

  const ReactMarkdown = require("react-markdown");
  const classes = useStyles();

  return (
    <>
      {loading && <h1>Loading...</h1>}
      {error && <h3>{error.message}</h3>}
      {data && (
        <>
          <Typography variant="h3" align="center">
            {`${data.note.title}`}
          </Typography>

          <Typography
            className={classes.subheading}
            variant="subtitle1"
            align="center"
          >
            {data.note.summary}
          </Typography>
          <Paper className={classes.noteContainer} elevation={0}>
            <ReactMarkdown source={data.note.bodyText} />
            <Button size="large">Delete</Button>
            <Button
              color="primary"
              variant="outlined"
              size="large"
              onClick={editToggle}
            >
              Edit
            </Button>
            {editOn && <h1>Edit On</h1>}
          </Paper>
        </>
      )}
    </>
  );
};
